<?php/** * @author Semenov Alexander <semenov@skeeks.com> * @link http://skeeks.com/ * @copyright 2010 SkeekS (СкикС) * @date 31.03.2016 */namespace v3project\themes\mega\components;use skeeks\cms\money\MoneyComponent;/** * @author Semenov Alexander <semenov@skeeks.com> */class CustomMoney extends MoneyComponent{    /**     * Сконвертировать и отформатировать для текущих настроек     *     * @param \skeeks\cms\money\Money $money     * @param null $language     * @param null $currency     * @return string     */    public function convertAndFormat($money, $language = null, $currency = null)    {        if ($money->getCurrency()->code != "RUB")        {            return parent::convertAndFormat($money, $language, $currency);        }        if (!$currency)        {            $currency = $this->currencyCode;        }        $money->convertToCurrency($currency);        $f = new \NumberFormatter(\Yii::$app->language, \NumberFormatter::DECIMAL);        $f->setAttribute(\NumberFormatter::MIN_FRACTION_DIGITS, 0);        return $f->format($money->amount) . " р.";    }}